select
Id, Counselling_Body
from CounsellingMaster where Id=:Id;

update CounsellingMaster set
Id=:Id, Counselling_Body=:Counselling_Body
where Id=:Id;

select
File_No, Counselling_Id, Roll_No, Rank, Category_Rank, Percentile
from counsellingDetail where File_No=:File_No;

insert into counsellingDetail
(File_No, Counselling_Id, Roll_No, Rank, Category_Rank, Percentile) values
(:File_No, :Counselling_Id, :Roll_No, :Rank, :Category_Rank, :Percentile);

update counsellingDetail set
File_No=:File_No, Counselling_Id=:Counselling_Id, Roll_No=:Roll_No, Rank=:Rank, Category_Rank=:Category_Rank, Percentile=:Percentile
where File_No=:File_No;
