insert into studentfeestaging ( File_No, Is_Reoccuring, Is_Conditional, FeeHead_Id, Amount, Created_By, Updated_By, Approved, Fee_Generated, Created_Date, Modified_Date
)
select sd.File_No ,fd.Fee_Head_Id,fd.Fee_Amount, :Created_By, :Created_By ,null,null, now(), now() from studentdetail sd join feeDetailmaster fd on sd.Semester=fd.Semester and sd.Course_Id=fd.Course
and sd.Branch_Id=fd.Branch where sd.File_No=:File_No;

select
File_No, Is_Reoccuring, Is_Conditional, FeeHead_Id, Amount, Created_By, Updated_By, Approved, Fee_Generated, Created_Date, Modified_Date
FROM studentfeestaging where File_No=:File_No and FeeHead_Id=ifNull(:FeeHead_Id,FeeHead_Id);

Update feedetailmaster set
COURSE=:COURSE, BRANCH=:BRANCH, FEE_HEAD_ID=:FEE_HEAD_ID, FEE_AMOUNT=:FEE_AMOUNT, Centre_Id=:Centre_Id, Shift_Id=:Shift_Id, Session_Id=:Session_Id
where COURSE=:COURSE AND BRANCH=:BRANCH AND FEE_HEAD_ID=:FEE_HEAD_ID ;

SELECT
File_No, Batch_Id, Session_Id, Component_Id, User, Created_Date, Remark, Mode, Amount
from feetransactiondebit where semester = (select max(SEMESTER) from feetransactiondebit where file_no=:File_No) and file_no=:File_No;

SELECT
File_No, Batch_Id, Session_Id, Component_Id, User, Created_Date, Remark, Mode, Amount
from feetransactionCredit where semester = (select max(SEMESTER) from feetransactionCredit where file_no=:File_No) and file_no=:File_No;

Select
Head_Id, Head, Transaction_Type, Parent_Type_Id, isReoccurring, Refund_Type
from feeDiscountHeadmaster where Head_Id=:Head_Id ;

Insert into feeDiscountHeadmaster
(Head_Id, Head, Transaction_Type, Parent_Type_Id, isReoccurring, Refund_Type) values
(:Head_Id, :Head, :Transaction_Type, :Parent_Type_Id, :isReoccurring, :Refund_Type);

Update feeDiscountHeadmaster set
Head_Id=:Head_Id, Head=:Head, Transaction_Type=:Transaction_Type, Parent_Type_Id=:Parent_Type_Id, isReoccurring=:isReoccurring, Refund_Type=:Refund_Type
where Head_Id=:Head_Id;
