insert into studentfeestaging ( File_No, Academic_Year, Semester, FeeHead_Id, Amount, Created_By, Updated_By, Approved, Fee_Generated, Created_Date, Modified_Date
)
select sd.File_No ,sd.Academic_Year,sd.Semester,fd.Fee_Head_Id,fd.Fee_Amount, :Created_By, :Updated_By ,null,null, now(), now() from studentdetail sd join feeDetailmaster fd on sd.Semester=fd.Semester and sd.Course_Id=fd.Course
and sd.Branch_Id=fd.Branch where sd.File_No=:File_No;

Update studentfeestaging set
FILE_NO=:FILE_NO, COURSE=:COURSE, BRANCH=:BRANCH, SEMESTER=:SEMESTER, FEE_GENERATED=:FEE_GENERATED
where FILE_NO=:FILE_NO;

Update feedetailmaster set
COURSE=:COURSE, BRANCH=:BRANCH, SEMESTER=:SEMESTER, FEE_HEAD_ID=:FEE_HEAD_ID, FEE_AMOUNT=:FEE_AMOUNT
where COURSE=:COURSE AND BRANCH=:BRANCH AND SEMESTER=:SEMESTER ;

select
File_No, Academic_Year, Semester, FeeHead_Id, Amount, Created_By, Updated_By, Approved, Fee_Generated, Created_Date, Modified_Date
FROM studentfeestaging where File_No=:File_No;

insert into studentfeestaging ( File_No, Academic_Year, Semester, FeeHead_Id, Amount, Created_By, Updated_By, Approved, Fee_Generated, Created_Date, Modified_Date
)
select :File_No,sd.Academic_Year,sd.Semester,fd.Fee_Head_Id,fd.Fee_Amount, :Created_By, :Updated_By, null, null, now(), now() from studentdetail sd join feeDetailmaster fd on sd.Semester=fd.Semester and sd.Course_Id=fd.Course
and sd.Branch_Id=fd.Branch where sd.File_No=:File_No;

Update studentfeestaging set
FILE_NO=:FILE_NO, COURSE=:COURSE, BRANCH=:BRANCH, SEMESTER=:SEMESTER, FEE_GENERATED=:FEE_GENERATED
where FILE_NO=:FILE_NO;

Update feedetailmaster set
COURSE=:COURSE, BRANCH=:BRANCH, SEMESTER=:SEMESTER, FEE_HEAD_ID=:FEE_HEAD_ID, FEE_AMOUNT=:FEE_AMOUNT
where FEE_HEAD_ID=:FEE_HEAD_ID AND COURSE=:COURSE AND BRANCH=:BRANCH;

Select
Head_Id, Head, Type, Parent_Type_Id, Discount_Type, Refund_Type
from feeDiscountHeadmaster where Head_Id=:Head_Id ;

Insert into feeDiscountHeadmaster
(Head_Id, Head, Type, Parent_Type_Id, Discount_Type, Refund_Type) values
(:Head_Id, :Head, :Type, :Parent_Type_Id, :Discount_Type, :Refund_Type);

Update feeDiscountHeadmaster set
Head_Id=:Head_Id, Head=:Head, Type=:Type, Parent_Type_Id=:Parent_Type_Id, Discount_Type=:Discount_Type, Refund_Type=:Refund_Type
where Head_Id=:Head_Id;
