Select
Wing, Floor, Block, Name, File_No, Room_No
from HostelAllocation where File_No=:File_No;

Update HostelAllocation set
Wing=:Wing, Floor=:Floor, Block=:Block, Name=:Name, File_No=:File_No, Room_No=:Room_No
where File_No=:File_No;

Select
Type_Code, Description, Threshold, Price, Room_Capacity
from HostelInventory where Type_Code=:Type_Code;

Insert into HostelInventory
(Type_Code, Description, Threshold, Price, Room_Capacity ) values
(:Type_Code, :Description, :Threshold, :Price, :Room_Capacity);

Update HostelInventory set
Type_Code=:Type_Code, Description=:Description, Threshold=:Threshold, Price=:Price, Room_Capacity=:Room_Capacity
where Type_Code=:Type_Code;

Select
File_No, Fee_Paid, Type_Code
from HostelReservation where File_No=:File_No;

Insert into HostelReservation
(File_No, Fee_Paid, Type_Code) values
(:File_No, :Fee_Paid, :Type_Code);

Update HostelReservation set
File_No=:File_No, Fee_Paid=:Fee_Paid, Type_Code=:Type_Code
where File_No=:File_No;

Select
Room_No, Type_Code
from RoomTypeDetail where Type_Code=:Type_Code;

Update RoomTypeDetail set
Room_No=:Room_No, Type_Code=:Type_Code
where Type_Code=:Type_Code;
