properties.entry:
select sd.File_No, sd.Registration_No, sb.First_Name, sb.Last_Name, sb.Father_name, sb.Gender, sb.DOB, sb.Enroll_No, sd.Course_Id, sd.Branch_Id, sb.Semester, sd.Academic_Year, sb.Batch_Id, sb.Section_Id, sb.Session_Id, sb.Shift_Id, sb.Centre_Id,
rtd.Room_No, rtd.Room_Capacity
from student_detail sd join room_Allocation_Detail rad on rad.file_no = sd.File_No join student_basic sb on sd.File_No = sb.FILE_NO join room_type_detail rtd on rad.Room_No = rtd.Room_No join Room_type_master rtm on rtd.room_Type_id = rtm.room_Type_id where rad.is_Allocated=1 and rtd.Room_No=:Room_No;
