Select
File_No, Room_No, Allocated_On, Allocated_By, Updated_By, Checkout_On, IsAllocated
from RoomAllocationDetail where File_No=:File_No;

Update RoomAllocationDetail set
File_No=File_No:, Room_No=:Room_No, Allocated_On=:Allocated_On, Allocated_By=:Allocated_By, Updated_By=:Updated_By, Checkout_On=:Checkout_On, IsAllocated=:IsAllocated
where File_No=:File_No;

Select
Type_Code, Description, Threshold, Price, Room_Capacity
from Roomtypemaster where Type_Code=:Type_Code;

Insert into Roomtypemaster
(Type_Code, Description, Threshold, Price, Room_Capacity ) values
(:Type_Code, :Description, :Threshold, :Price, :Room_Capacity);

Update Roomtypemaster set
Type_Code=:Type_Code, Description=:Description, Threshold=:Threshold, Price=:Price, Room_Capacity=:Room_Capacity
where Type_Code=:Type_Code;

Select
hr.File_No, hr.Fee_Paid, hr.Type_Code, hr.Allocation_Status, hr.Is_Active, hi.Description, hi.Price
from HostelReservation hr join RoomTypeMaster hi on hi.Type_Code = hr.Type_Code where File_No=:File_No;

Insert into HostelReservation
(File_No, Fee_Paid, Type_Code, Allocation_Status, Is_Active) values
(:File_No, :Fee_Paid, :Type_Code, :Allocation_Status, :Is_Active);

Update HostelReservation set
File_No=:File_No, Fee_Paid=:Fee_Paid, Type_Code=:Type_Code, Allocation_Status=:Allocation_Status, Is_Active=:Is_Active
where File_No=:File_No;

Select
Room_No, Type_Code, Wing_Id, Floor_Id, Block_Id
from RoomTypeDetail where Type_Code=:Type_Code;

Insert into RoomTypeDetail
(Room_No, Type_Code, Wing_Id, Floor_Id, Block_Id ) values
(:Room_No, :Type_Code, :Wing_Id, :Floor_Id, :Block_Id );

Update RoomTypeDetail set
Room_No=:Room_No, Type_Code=:Type_Code, Wing_Id=:Wing_Id, Floor_Id=:Floor_Id, Block_Id=:Block_Id
where Type_Code=:Type_Code;
