Select
File_No, Amount, State_Id, Remark, Approved, Created_Date, Is_Reoccurring, Is_Conditional, Parent_Income
from scholarshipdetail where File_No=:File_No;

Select
File_No, Amount_Received, Receiving_Date
from ScholarshipPaymentDetail where File_No=:File_No;



Insert into ScholarshipPaymentDetail

	(File_No,
	Amount_Received,
	Receiving_Date
	) values

	(:File_No,
	:Amount_Received,
	:Receiving_Date
	);

Update ScholarshipPaymentDetail set
File_No=:File_No, Amount_Received=:Amount_Received, Receiving_Date=:Receiving_Date
where File_No=:File_No;
