properties.entry:
select
sum(vd.Capacity) as Capacity,
rm.Description, count(tr.Route_Stop_Id) as Reserved_Seat, sum(vd.Capacity) - count(tr.Route_Stop_Id) as available_seat from vehicle_detail vd left join route_master rm on vd.Route_Id = rm.Route_Id left join transport_reservation tr on rm.Route_Id = tr.Route_Stop_Id group by rm.route_id;
