properties.entry:

Select

tm.Threshold,tm.Route_Code, tm.Description, tm.Price,count(tr.Route_Code) as Reserved_Seat,tm.Threshold-count(tr.Route_Code) as Available_Seat

from TransportMaster tm join transportReservation tr on tm.Route_Code=tr.Route_Code group by tr.Route_Code;

Select
Route_Code , Description, Threshold, Price, Is_Active, Allocation_Status
from TransportMaster where Route_Code=:Route_Code;

Insert into TransportMaster
(Route_Code, Description, Threshold, Price, Is_Active, Allocation_Status ) values
(:Route_Code, :Description, :Threshold, :Price, :Is_Active, :Allocation_Status
);

Update TransportMaster set
Description=:Description, Threshold=:Threshold, Price=:Price, Is_Active=:Is_Active, Allocation_Status=:Allocation_Status
where Route_Code=:Route_Code;

select
File_No, Fee_Paid, Route_Code
from TransportReservation where File_No=:File_No;

Insert into TransportReservation
( File_No, Fee_Paid, Route_Code)
values (:File_No, :Fee_Paid, :Route_Code);

Update TransportReservation set
Fee_Paid=:Fee_Paid, Route_Code=:Route_Code
where File_No=:File_No;

select
File_No, Vehicle_Id
from TransportAllocation where File_No=:File_No;



Update TransportAllocation set  

Vehicle_Id=:Vehicle_Id

where File_No=:File_No;

select
Type, Capacity , Vehicle_No, Route_Code, Vehicle_Id
from VehicleDetail where Vehicle_Id=:Vehicle_Id;

insert into VehicleDetail
(Type, Capacity , Vehicle_No, Route_Code, Vehicle_Id) values
(:Type, :Capacity, :Vehicle_No, :Route_Code, :Vehicle_Id);

update VehicleDetail set
Type=:Type, Capacity=:Capacity , Vehicle_No=:Vehicle_No, Route_Code=:Route_Code
where Vehicle_Id=:Vehicle_Id;
