properties.entry:
Select
Id, Name, Primary_Contact_No, Secondary_contact_No, Address, Email_Id
from ConsultantMaster where Id=:Id;

Select
File_No, Consultant_Id, Consultancy_Agreed, Payment_Mode, Amount_To_Pay, Due_Date
from ConsultantDetail where File_NO=:File_No and Consultant_Id=:Consultant_Id;

Update ConsultantDetail set
Consultancy_Agreed=:Consultancy_Agreed, Payment_Mode=:Payment_Mode, Amount_To_Pay=:Amount_To_Pay, Due_Date=:Due_Date
where File_NO=:File_No and Consultant_Id=:Consultant_Id;

Select
Amount, Pay_Date, FileNo
from ConsultantPaymentDetail where File_No=:File_No;

Insert into ConsultantPaymentDetail
(FileNo, Amount, Pay_Date) values
(:FileNo, :Amount, :Pay_Date);

Update ConsultantPaymentDetail set
Amount=:Amount, Pay_Date=:Pay_Date
where File_No=:File_No;
