Select

    Subject_Id, Subject
    
From SubjectMaster where Subject_Id=:Subject_Id;

    
      
Insert into SubjectMaster 

   (Subject_Id, Subject) values    
    
(:Subject_Id, :Subject);

Select
Id, Code, Description
from QuotaCodeMaster where Id=:Id;

    
      
Insert into QuotaCodeMaster

    (Id, Code, Description) values
    
 (:Id, :Code, :Description);

   
     
Update QuotaCodeMaster set         
   
   Id=:Id, Code=:Code, Description=:description

where Id=:Id;
